if ( class_exists( 'WC_Emails') ) {
    $email_types = WC()->mailer()->get_emails();
    $get_order_status_html = function( $seleted_statuses, $option_key ) use ( $email_types ) {
        ob_start();
        foreach ( $email_types as $email ) {
            ?>
            <div class="_winvoice-custom-control _winvoice-custom-checkbox">

                <input type="checkbox" name="<?php echo esc_attr( $option_key ).'[]' ?>"
                       class="_winvoice-custom-control-input" id="<?php echo esc_attr( $email->id ).$option_key ?>"
                       value="<?php echo esc_attr( $email->id ) ?>"
                    <?php
                    if ( in_array( $email->id, $seleted_statuses ) ) {
                        echo 'checked';
                    }
                    ?>
                >
                <label class="_winvoice-custom-control-label"
                       for="<?php echo esc_attr( $email->id ).$option_key ?>"><?php esc_html_e( $email->title, 'webappick-pdf-invoice-for-woocommerce' ); ?></label>
            </div>
            <?php
        }
        ?>
        <div class="_winvoice-custom-control _winvoice-custom-checkbox">

            <input type="checkbox" name="<?php echo esc_attr( $option_key ).'[]' ?>"
                   class="_winvoice-custom-control-input" id="always_allow<?php echo $option_key ?>"
                   value="always_allow"
                <?php
                if ( in_array( 'always_allow', $seleted_statuses ) ) {
                    echo 'checked';
                }
                ?>
            >
            <label class="_winvoice-custom-control-label"
                   for="always_allow<?php echo $option_key ?>"><?php esc_html_e( 'Always Allow', 'webappick-pdf-invoice-for-woocommerce' ); ?></label>
        </div>
        <?php
        $attachment_statuses_html = ob_get_contents();
        ob_end_clean();

        return $attachment_statuses_html;
    };

}




